#!/bin/bash# Disable access to linux system accounts by regular users.# Use this to check for current system account access:# egrep -v "^\+" /etc/passwd | awk -F: '($1!="root" && $1!="sync" && $1!="shutdown" && $1!="halt" && $3<500 && $7!="/usr/sbin/nologin" && $7!="/bin/false") {print}'for user in `awk -F: '($3 < 500) {print $1 }' /etc/passwd`; do  if [ $user != "root" ]  then    /usr/sbin/usermod -L $user    if [ $user != "sync" ] && [ $user != "shutdown" ] && [ $user != "halt" ]    then      /usr/sbin/usermod -s /usr/sbin/nologin $user    fi  fidone